query:
mutation ($locationName: String!, $characterName: String!, $episodeName: String!) {
  createLocation(location: {name: $locationName, type: "Country", dimension: "Big"}) {
    id
  }
  createCharacter(character: {name: $characterName, type: "man", status: "OK", species: "human", gender: "male", image: "png", originId: 14393, locationId: 838}) {
    id
  }
  createEpisode(episode: {name: $episodeName, air_date: "27th Oct", episode: "E2"}) {
    id
  }
  deleteLocations(locationIds: [14396, 14395]) {
    locationsDeleted
  }
}
